import{_ as a,c as e,o as t,N as r}from"./chunks/framework.I0_e-zsE.js";const f=JSON.parse('{"title":"Introducing Uniq Avatars","description":"","frontmatter":{"title":"Introducing Uniq Avatars","outline":[0,4],"order":1},"headers":[],"relativePath":"tutorials/uniq-factories/uniq-avatar/index.md","filePath":"tutorials/uniq-factories/uniq-avatar/index.md","lastUpdated":null}'),i={name:"tutorials/uniq-factories/uniq-avatar/index.md"},n=r('<h1 id="introducing-uniq-avatars" tabindex="-1">Introducing Uniq Avatars <a class="header-anchor" href="#introducing-uniq-avatars" aria-label="Permalink to &quot;Introducing Uniq Avatars&quot;">​</a></h1><p>The Ultra Avatar contract, <code>ultra.avatar</code> allows users to set an Uniq as their avatar.</p><p>The user sets an avatar using the <code>setavatar</code> action and it is then availabe for anyone using Ultra&#39;s blockchain to view and use. They read data from the contract and render the Uniq inside their app.</p><p>Users can change their avatar with the same action or remove avatar completely with the <code>clearavatar</code> action.</p><p>If a user loses possession of the Uniq through transfer or burn actions, it can no longer be used as an avatar and an active avatar with such a Uniq will be cleared.</p><h2 id="what-are-uniq-avatars-for" tabindex="-1">What are Uniq Avatars for? <a class="header-anchor" href="#what-are-uniq-avatars-for" aria-label="Permalink to &quot;What are Uniq Avatars for?&quot;">​</a></h2><p>We intend for Uniq Avatars to be used within Ultra as cross-ecosystem visual identifiers of users and their identity. This means that if you see someone with a Uniq as their avatar in a game or an application, you can rest assured that <strong>it is verifiable</strong>.</p><h2 id="developer-use" tabindex="-1">Developer Use <a class="header-anchor" href="#developer-use" aria-label="Permalink to &quot;Developer Use&quot;">​</a></h2><p>For developers we provide details about the two actions available via the on-chain ABI.</p><ul><li><a href="./setavatar.html">setavatar</a></li><li><a href="./clearavatar.html">clearavatar</a></li></ul>',10),o=[n];function s(c,l,d,h,u,v){return t(),e("div",null,o)}const q=a(i,[["render",s]]);export{f as __pageData,q as default};
